import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";import{o as e,c as h,a as p}from"./app.edcb9e1a.js";const r={},d=p('<h2 id="\u5D29\u574F\u5B9A\u4E49" tabindex="-1"><a class="header-anchor" href="#\u5D29\u574F\u5B9A\u4E49" aria-hidden="true">#</a> \u5D29\u574F\u5B9A\u4E49</h2><p>\u5D29\u574F\u53D1\u751F\u65F6\uFF0C\u9B54\u6CD5\u5C11\u5973\u7684\u9B54\u529B\u4F1A\u65E0\u5DEE\u522B\u5730\u5411\u5468\u56F4\u91CA\u653E\uFF0C\u8FD9\u4F1A\u4EA7\u751F\u4E00\u4E2A\u7C7B\u4F3C\u7ED3\u754C\u7684\u7A7A\u95F4\uFF0C\u5E76\u5BF9\u5176\u4E2D\u7684\u9B54\u6CD5\u5C11\u5973\u548C\u654C\u4EBA\u9020\u6210\u4F24\u5BB3\u3002\u5D29\u574F\u53D1\u52A8\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u9B54\u6CD5\u5C11\u5973\u4F1A\u5931\u53BB\u610F\u8BC6\uFF0C\u8FD9\u610F\u5473\u7740\u73A9\u5BB6\u4E0D\u53EF\u4EE5\u64CD\u4F5C\u9B54\u6CD5\u5C11\u5973\uFF0C\u5E76\u4E14\u6B64\u65F6\u7684\u9B54\u6CD5\u5C11\u5973\u7684\u672C\u4F53\u5E94\u6309\u7167\u975E\u751F\u7269\u6765\u5224\u5B9A\u3002</p><p>\u5728\u5176\u4ED6\u9B54\u6CD5\u5C11\u5973\u8D76\u6765\u89E3\u9664\u5D29\u574F\u4E4B\u524D\uFF0C\u5D29\u574F\u4E2D\u7684\u9B54\u6CD5\u5C11\u5973\u4F1A\u4E00\u76F4\u4FDD\u6301\u8BE5\u72B6\u6001\u3002</p><h3 id="\u73AF\u5883\u7834\u574F" tabindex="-1"><a class="header-anchor" href="#\u73AF\u5883\u7834\u574F" aria-hidden="true">#</a> \u73AF\u5883\u7834\u574F</h3><p>\u5F53\u5D29\u574F\u53D1\u751F\u65F6\uFF0C\u9B54\u6CD5\u5C11\u5973\u4F1A\u5BF9\u5468\u56F4\u6240\u6709\u4EBA\u548C\u5BF9\u8C61\u9020\u62101d\u5269\u4F59MP\u7684\u4F24\u5BB3\uFF0C\u5982\u679C\u6B64\u65F6\u9B54\u6CD5\u5C11\u5973\u8FD8\u6709\u5269\u4F59SAN\u503C\uFF0C\u5219\u4F1A\u5168\u90E8\u8F6C\u5316\u4E3AMP\u8BA1\u7B97\u3002\u540C\u65F6\u4F1A\u6839\u636E\u81EA\u8EAB\u62E5\u6709\u7684\u80FD\u529B\u5BF9\u5468\u56F4\u73AF\u5883\u9020\u6210\u6539\u53D8</p><h2 id="\u9B54\u6CD5\u5C11\u5973\u9677\u5165\u5D29\u574F" tabindex="-1"><a class="header-anchor" href="#\u9B54\u6CD5\u5C11\u5973\u9677\u5165\u5D29\u574F" aria-hidden="true">#</a> \u9B54\u6CD5\u5C11\u5973\u9677\u5165\u5D29\u574F</h2><p>\u5F53\u5728\u6218\u6597\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u9B54\u6CD5\u5C11\u5973\u53EF\u80FD\u4F1A\u4E3B\u52A8\u6216\u88AB\u52A8\u5730\u9677\u5165\u5D29\u574F\u3002\u6B64\u65F6\u5D29\u574F\u7ED3\u754C\u5C1A\u672A\u5B8C\u6210\uFF0C\u5176\u4ED6\u73A9\u5BB6\u53EF\u4EE5\u8F7B\u677E\u5730\u53D1\u73B0\u534A\u9B54\u5973\u7684\u5B58\u5728\uFF0C\u800C\u4E0D\u9700\u8981\u7ECF\u8FC7\u590D\u6742\u7684\u63A2\u7D22\u3002</p><p>\u4ECE\u5D29\u574F\u4E2D\u6062\u590D\u7684\u9B54\u6CD5\u5C11\u5973\u4F1A\u635F\u5931\u4E00\u90E8\u5206\u91CD\u8981\u7684\u8BB0\u5FC6\uFF0C\u4F46\u8FD9\u5E76\u4E0D\u662F\u8BB0\u5FC6\u80FD\u529B\u7684\u4E27\u5931\uFF0C\u800C\u662F\u8BA4\u4E3A\u81EA\u5DF1\u4ECE\u672A\u7ECF\u5386\u8FC7\u8FD9\u4E9B\u4E8B\u60C5\u6216\u662F\u8BA4\u8BC6\u67D0\u4EBA\u3002\uFF08\u56E0\u4E3A\u8FD9\u90E8\u5206\u8BB0\u5FC6\u53D8\u6210\u4E86\u5D29\u574F\uFF09\u9B54\u6CD5\u5C11\u5973\u7684\u611F\u60C5\u5C5E\u6027\u4F1A\u4E0B\u964D1D6\uFF0C\u5E76\u91CD\u65B0\u8BA1\u7B97\u76F8\u5173\u7684\u5C5E\u6027\u6216\u4E0A\u9650\uFF0C\u5DF2\u7ECF\u5206\u914D\u7684\u6280\u80FD\u70B9\u4E0D\u4F1A\u6539\u53D8\u3002</p><p>\u6211\u4EEC\u9F13\u52B1\u73A9\u5BB6\u81EA\u5DF1\u63CF\u8FF0\u81EA\u5DF1\u7684\u9B54\u6CD5\u5C11\u5973\u4F1A\u5F62\u6210\u600E\u6837\u7684\u5D29\u574F\u7ED3\u754C\uFF0C\u5728\u53D8\u6210\u534A\u9B54\u5973\u540E\u62E5\u6709\u600E\u6837\u7684\u80FD\u529B\u3002\u5982\u679C\u73A9\u5BB6\u81EA\u5DF1\u6CA1\u6709\u6E05\u695A\u7684\u60F3\u6CD5\uFF0C\u4E3B\u6301\u4EBA\u53EF\u4EE5\u6839\u636E\u9B54\u6CD5\u5C11\u5973\u7684\u80CC\u666F\u548C\u80FD\u529B\u62DF\u5B9A\u4E00\u4E2A\u65B9\u6848\u5E76\u5728\u83B7\u5F97\u73A9\u5BB6\u7684\u540C\u610F\u540E\u4F7F\u7528\u3002</p><h2 id="\u5728\u5D29\u574F\u4E2D\u63A2\u7D22" tabindex="-1"><a class="header-anchor" href="#\u5728\u5D29\u574F\u4E2D\u63A2\u7D22" aria-hidden="true">#</a> \u5728\u5D29\u574F\u4E2D\u63A2\u7D22</h2><p>\u9B54\u6CD5\u5C11\u5973\u5728\u5D29\u574F\u72B6\u6001\u65F6\u4F1A\u9677\u5165\u5669\u68A6\uFF0C\u8FD9\u4F7F\u5F97\u5979\u4EEC\u65E0\u6CD5\u56DE\u60F3\u8D77\u81EA\u5DF1\u7F8E\u597D\u7684\u8BB0\u5FC6\u3002\u5728\u5D29\u574F\u4E2D\uFF0C\u8FD9\u8868\u73B0\u4E3A\u9B54\u6CD5\u5C11\u5973\u4F1A\u5C06\u73CD\u89C6\u7684\u7269\u4F53\u9690\u85CF\u8D77\u6765\u3002\u73A9\u5BB6\u9700\u8981\u6839\u636E\u73AF\u5883\u4E2D\u7684\u7EBF\u7D22\uFF0C\u901A\u8FC7\u89E3\u5BC6\u6216\u662F\u63A2\u7D22\uFF0C\u627E\u5230\u8FD9\u4E9B\u7269\u4F53\u6216\u662F\u7834\u574F\u4E00\u4E9B\u5FC3\u7ED3\u3002\u8FD9\u90E8\u5206\u4E3B\u6301\u4EBA\u548C\u6A21\u7EC4\u4F5C\u8005\u53EF\u4EE5\u6839\u636E\u81EA\u5DF1\u7684\u4E60\u60EF\u5C3D\u60C5\u53D1\u6325</p><p>\u800C\u5728\u6240\u6709\u76EE\u6807\u70B9\u4E2D\uFF0C\u6709\u4E00\u5904\u4F1A\u662F\u534A\u9B54\u5973\u7684\u85CF\u8EAB\u5904\u3002\u73A9\u5BB6\u89E3\u5F00\u6B64\u5904\u7684\u8C1C\u9898\u540E\uFF0C\u4F1A\u88AB\u5F3A\u5236\u62C9\u5165\u4E0E\u534A\u9B54\u5973\u7684\u6218\u6597\u3002</p><p>\u73A9\u5BB6\u5728\u7ED3\u754C\u4E2D\u63A2\u7D22\u65F6\uFF0C\u53EF\u80FD\u4F1A\u627E\u5230\u9B54\u6CD5\u5C11\u5973\u7684\u8EAB\u4F53\u3002\u5F53\u73A9\u5BB6\u653B\u51FB\u9B54\u6CD5\u5C11\u5973\u7684\u8EAB\u4F53\u65F6\uFF0C\u534A\u9B54\u5973\u4F1A\u88AB\u5F3A\u5236\u547C\u5524\u51FA\u6765\u4EE5\u4FDD\u62A4\u9B54\u6CD5\u5C11\u5973\u3002\u4F46\u662F\u5BF9\u9B54\u6CD5\u5C11\u5973\u8FD9\u4E9B\u4F24\u5BB3\u90FD\u4F1A\u8BB0\u5F55\u4E0B\u6765\uFF0C\u5E76\u5B9E\u65F6\u53CD\u5E94\u5728\u9B54\u6CD5\u5C11\u5973\u4E0A\u3002\u82E5\u9B54\u6CD5\u5C11\u5973\u7684HP\u4E0B\u964D\u81F30\uFF0C\u9B54\u6CD5\u5C11\u5973\u4F1A\u6B7B\u4EA1\uFF0C\u4E0E\u6B64\u540C\u65F6\u5D29\u574F\u4E5F\u4F1A\u7ED3\u675F\u3002\u82E5\u9B54\u6CD5\u5C11\u5973\u91C7\u7528\u8FD9\u79CD\u65B9\u5F0F\u89E3\u51B3\u4E86\u5D29\u574F\uFF0C\u9700\u8981\u8FDB\u884C\u4E00\u4E2A1d4/1d8\u7684SC\uFF0C\u89C6\u73A9\u5BB6\u4E0E\u8BE5\u9B54\u6CD5\u5C11\u5973\u7684\u4EB2\u5BC6\u7A0B\u5EA6\uFF0C\u4E3B\u6301\u4EBA\u53EF\u4EE5\u8BBE\u7F6E\u989D\u5916\u7684SAN\u503C\u635F\u5931\u3002</p><p>\u5728\u4E0E\u5D29\u574F\u7684\u6218\u6597\u7ED3\u675F\u540E\uFF0C\u9B54\u6CD5\u5C11\u5973\u4F1A\u83B7\u5F971D5\u7684\u201C\u5D29\u574F\u8BA4\u77E5\u201D\u6280\u80FD\u70B9\uFF0C\u901A\u8FC7\u6B64\u65B9\u6CD5\u83B7\u5F97\u201C\u5D29\u574F\u8BA4\u77E5\u201D\u6280\u80FD\u70B9\u4E0D\u4F1A\u6263\u9664san\u503C\uFF0C\u73A9\u5BB6\u9700\u8981\u7ACB\u5373\u5C06\u83B7\u5F97\u7684\u6280\u80FD\u70B9\u5206\u914D\u81F3\u6218\u6597\u6280\u80FD\u3002\u6211\u4EEC\u4E0D\u5EFA\u8BAE\u5728\u5C06\u67D0\u9879\u6280\u80FD\u70B9\u7684\u8FC7\u9AD8\uFF0C\u8FD9\u53EF\u80FD\u4F1A\u5BFC\u81F4\u67D0\u5F20\u5361\u7684\u5F3A\u5EA6\u8D85\u6A21\u800C\u4E0D\u88AB\u4F7F\u7528D100\u89C4\u5219\u7684\u4E3B\u6301\u4EBA\u5141\u8BB8\u4F7F\u7528\uFF08\u5728\u6269\u5C55\u89C4\u5219-RD\u89C4\u5219\u4E2D\u4E0D\u5B58\u5728\u6B64\u95EE\u9898\uFF09</p><p>\u5982\u679C\u5728\u4E0E\u534A\u9B54\u5973\u7684\u6218\u6597\u4E2D\u653B\u51FB\u9B54\u6CD5\u5C11\u5973\u7684\u672C\u4F53\uFF0C\u534A\u9B54\u5973\u4F1A\u89E3\u9664\u5F53\u524D\u6240\u6709\u5F02\u5E38\u72B6\u6001\uFF0C\u5E76\u6253\u65AD\u81EA\u5DF1\u7684\u84C4\u529B\uFF0C\u77AC\u79FB\u5230\u672C\u4F53\u7684\u8EAB\u8FB9\u8FDB\u5165\u72C2\u5316\u72B6\u6001\u5168\u529B\u4FDD\u62A4\u672C\u4F53\u3002</p><h3 id="\u5171\u9E23" tabindex="-1"><a class="header-anchor" href="#\u5171\u9E23" aria-hidden="true">#</a> \u5171\u9E23</h3><p>\u5171\u9E23\u662F\u9B54\u6CD5\u5C11\u5973\u4F7F\u7528\u80FD\u529B\u7684\u9014\u5F84\u4E4B\u4E00\u3002\u5171\u9E23\u53EF\u4EE5\u4F7F\u9B54\u6CD5\u5C11\u5973\u80FD\u591F\u611F\u77E5\u81EA\u8EAB\u548C\u4ED6\u4EBA\u7684\u9B54\u529B\u5E76\u4E0E\u4E4B\u53CD\u5E94\u3002\u5171\u9E23\u4E0D\u9700\u8981\u9B54\u6CD5\u5C11\u5973\u53BB\u5B66\u4E60\uFF0C\u8FD9\u662F\u9B54\u6CD5\u5C11\u5973\u81EA\u8EAB\u56FA\u6709\u7684\u80FD\u529B\u3002</p><h3 id="\u8BB0\u5FC6\u7ED3\u6676" tabindex="-1"><a class="header-anchor" href="#\u8BB0\u5FC6\u7ED3\u6676" aria-hidden="true">#</a> \u8BB0\u5FC6\u7ED3\u6676</h3><p>\u73A9\u5BB6\u5728\u5D29\u574F\u7684\u63A2\u7D22\u8FC7\u7A0B\u4E2D\uFF0C\u4F1A\u83B7\u5F97\u5173\u4E8E\u9B54\u6CD5\u5C11\u5973\u8FC7\u53BB\u7684\u7EBF\u7D22\uFF0C\u8FD9\u4E9B\u7EBF\u7D22\u5C06\u4F1A\u4EE5\u8BB0\u5FC6\u7ED3\u6676\u7684\u5F62\u5F0F\u8BB0\u5F55\u4E0B\u6765\uFF0C\u5E76\u7531\u4E3B\u6301\u4EBA\u8BF4\u660E\u8BB0\u5FC6\u7ED3\u6676\u53CA\u5176\u4EE3\u8868\u7684\u5185\u5BB9\u3002\u8BB0\u5FC6\u7ED3\u6676\u662F\u4E00\u4E2A\u53EF\u4EE5\u62E5\u6709\u5F62\u6001\u7684\u7269\u54C1\u53EA\u80FD\u7531\u4E00\u4EBA\u6301\u6709\uFF0C\u4E5F\u53EF\u4EE5\u662F\u53EA\u662F\u4E00\u6761\u4FE1\u606F\u80FD\u591F\u5728\u591A\u4EBA\u95F4\u5171\u4EAB\u3002\u5728\u4E0E\u5D29\u574F\u7684\u6218\u6597\u4E2D\uFF0C\u73A9\u5BB6\u53EF\u4EE5\u4EE5\u8BB0\u5FC6\u7ED3\u6676\u4E3A\u5A92\u4ECB\uFF0C\u4E0E\u5D29\u574F\u8FDB\u884C\u5171\u9E23\u3002\u8FD9\u4E9B\u8BB0\u5FC6\u7ED3\u6676\u53EF\u4EE5\u6253\u65AD\u5D29\u574F\u5316\u8EAB\u7684\u884C\u52A8\uFF0C\u5E76\u7ED9\u5D29\u574F\u9644\u52A0\u4E00\u5B9A\u7684\u5F02\u5E38\u72B6\u6001\uFF0C\u4F46\u662F\u8BE5\u5F02\u5E38\u72B6\u6001\u53EA\u6709\u4E3B\u6301\u4EBA\u77E5\u9053\u3002\u4E3B\u6301\u4EBA\u53EF\u4EE5\u9F13\u52B1\u73A9\u5BB6\u53BB\u63CF\u8FF0\uFF0C\u81EA\u5DF1\u5C06\u5982\u4F55\u4EE5\u8BE5\u8BB0\u5FC6\u7ED3\u6676\u4E3A\u7A81\u7834\u70B9\uFF0C\u53BB\u5C1D\u8BD5\u5524\u9192\u9B54\u6CD5\u5C11\u5973\u7684\u610F\u8BC6\u3002\u8BB0\u5FC6\u7ED3\u6676\u5728\u4F7F\u7528\u8FC7\u4E00\u6B21\u540E\uFF0C\u4FBF\u4F1A\u5931\u6548\uFF0C\u73A9\u5BB6\u9700\u8981\u81EA\u884C\u5224\u65AD\u4F7F\u7528\u8BB0\u5FC6\u7ED3\u6676\u7684\u65F6\u673A\u3002</p><h3 id="\u8BB0\u5FC6" tabindex="-1"><a class="header-anchor" href="#\u8BB0\u5FC6" aria-hidden="true">#</a> \u8BB0\u5FC6</h3><p>\u5D29\u574F\u662F\u9B54\u6CD5\u5C11\u5973\u4E00\u90E8\u5206\u8BB0\u5FC6\u7684\u9B54\u5973\u5316\uFF0C\u56E0\u6B64\uFF0C\u5F53\u9B54\u6CD5\u5C11\u5973\u4E0E\u7ED3\u754C\u4E2D\u7684\u7269\u4F53\u5171\u9E23\u65F6\uFF0C\u53EF\u4EE5\u76F4\u63A5\u611F\u53D7\u5230\u8FD9\u90E8\u5206\u8BB0\u5FC6\uFF0C\u4ECE\u800C\u53D1\u73B0\u7ED3\u754C\u4E2D\u7684\u5F02\u5E38\u4E4B\u5904\u3002</p><p>\u9B54\u6CD5\u5C11\u5973\u4E0E\u7ED3\u754C\u4E2D\u7684\u7269\u4F53\u5171\u9E23\u65F6\uFF0C\u9700\u8981\u8FDB\u884C\u611F\u60C5\u5C5E\u6027\u7684\u68C0\u5B9A\u3002\u5F53\u68C0\u5B9A\u6210\u529F\u65F6\uFF0C\u5D29\u574F\u7684\u8BB0\u5FC6\u4F1A\u76F4\u63A5\u51FA\u73B0\u5728\u9B54\u6CD5\u5C11\u5973\u7684\u8111\u6D77\u4E2D\u3002\u6B64\u65F6\u9B54\u6CD5\u5C11\u5973\u5C06\u4F1A\u8FDB\u5165\u8FD9\u6BB5\u8BB0\u5FC6\u4E2D\u63A2\u7D22\uFF0C\u8FD9\u53EF\u80FD\u4F1A\u9047\u5230\u654C\u4EBA\uFF0C\u4E5F\u53EF\u80FD\u4F1A\u9047\u5230\u4E00\u4E9B\u7EBF\u7D22\u3002\u4F46\u662F\u65E0\u8BBA\u9B54\u6CD5\u5C11\u5973\u5728\u8BB0\u5FC6\u4E2D\u505C\u7559\u591A\u4E45\uFF0C\u5728\u5176\u4ED6\u73A9\u5BB6\u773C\u4E2D\uFF0C\u8FD9\u4E0D\u8FC7\u90FD\u662F\u4E00\u77AC\u95F4\u3002\u5F53\u68C0\u5B9A\u5931\u8D25\u65F6\uFF0C\u9B54\u6CD5\u5C11\u5973\u4F1A\u56E0\u4E3A\u8BB0\u5FC6\u7684\u77ED\u65F6\u95F4\u9519\u4E71\u800C\u9677\u5165\u5934\u6655\uFF0C\u5934\u6655\u4F1A\u6301\u7EED\u6570\u5206\u949F\u6216\u4E00\u4E2A\u6218\u6597\u8F6E\uFF0C\u5728\u6B64\u671F\u95F4\u65E0\u6CD5\u8FDB\u884C\u5171\u9E23\uFF0C\u4F7F\u7528\u5176\u4ED6\u6280\u80FD\u4E5F\u4F1A\u83B7\u5F97\u4E00\u4E2A\u60E9\u7F5A\u9AB0</p><p>\u4E3B\u6301\u4EBA\u53EF\u4EE5\u6839\u636E\u8BB0\u5FC6\u7684\u5185\u5BB9\u8BBE\u7F6Esan\u503C\u68C0\u5B9A\u3002\u9B54\u6CD5\u5C11\u5973\u53EF\u4EE5\u81EA\u884C\u51B3\u5B9A\u662F\u5426\u5C06\u8BB0\u5FC6\u7684\u5185\u5BB9\u544A\u8BC9\u5176\u4ED6\u4EBA\uFF0C\u4E5F\u53EF\u4EE5\u5728\u8F6C\u8FF0\u7684\u8FC7\u7A0B\u4E2D\u5BF9\u5185\u5BB9\u8FDB\u884C\u9690\u85CF\u6216\u7BE1\u6539\u3002</p><p>\u5F53\u67D0\u4E2A\u7269\u4F53\u6240\u643A\u5E26\u7684\u8BB0\u5FC6\u8DB3\u591F\u5F3A\u70C8\u65F6\uFF0C\u53EF\u80FD\u4F1A\u4F7F\u63A5\u8FD1\u6216\u89E6\u78B0\u7684\u9B54\u6CD5\u5C11\u5973\u5F3A\u5236\u8FDB\u5165\u5BF9\u5E94\u7684\u8BB0\u5FC6\u3002</p><h3 id="\u8FDE\u643A" tabindex="-1"><a class="header-anchor" href="#\u8FDE\u643A" aria-hidden="true">#</a> \u8FDE\u643A</h3><p>\u5F53\u9B54\u6CD5\u5C11\u5973\u8FDB\u5165\u5171\u9E23\u4E2D\u7684\u6218\u6597\u65F6\uFF0C\u53EF\u4EE5\u4F7F\u7528\u5176\u4ED6\u9B54\u6CD5\u5C11\u5973\u7684\u56FA\u6709\u80FD\u529B\u751A\u81F3\u6B66\u5668\u6765\u8FDB\u884C\u6218\u6597\uFF0C\u8FD9\u8868\u73B0\u4E3A\u9B54\u6CD5\u5C11\u5973\u4E4B\u95F4\u7684\u7275\u7ECA\u5728\u5176\u6218\u6597\u7684\u8FC7\u7A0B\u4E2D\u7ED9\u4E86\u5979\u529B\u91CF\u3002\u82E5\u73A9\u5BB6\u60F3\u8981\u4F7F\u7528\u8FDE\u643A\uFF0C\u5219\u9700\u8981\u548C\u4E3B\u6301\u4EBA\u8BF4\u660E\u60F3\u8981\u548C\u54EA\u4F4D\u9B54\u6CD5\u5C11\u5973\u8FDB\u884C\u8FDE\u643A\uFF0C\u4E3B\u6301\u4EBA\u53EF\u4EE5\u6839\u636E\u4E24\u4E2A\u9B54\u6CD5\u5C11\u5973\u95F4\u7684\u4EB2\u5BC6\u7A0B\u5EA6\u8FDB\u884C\u8BB0\u5FC6\u5C5E\u6027\u7684\u68C0\u5B9A\u3002\u5982\u679C\u68C0\u5B9A\u6210\u529F\uFF0C\u5219\u5728\u5171\u9E23\u4E2D\u7684\u9B54\u6CD5\u5C11\u5973\u80FD\u591F\u81EA\u7531\u4F7F\u7528\u53E6\u4E00\u4E2A\u9B54\u6CD5\u5C11\u5973\u7684\u6B66\u5668\u548C\u56FA\u6709\u80FD\u529B\u3002\u4F46\u662F\u6B66\u5668\u7684\u547D\u4E2D\u7387\u4F1A\u6309\u7167\u76F8\u8FD1\u7C7B\u578B\u7684\u6B66\u5668\u7CBE\u901A\u8BA1\u7B97\u800C\u975E\u672C\u547D\u6B66\u5668\u3002</p><h2 id="\u654C\u4EBA" tabindex="-1"><a class="header-anchor" href="#\u654C\u4EBA" aria-hidden="true">#</a> \u654C\u4EBA</h2><p>\u654C\u4EBA\u4E0D\u8BA1\u7B97MP\u548CDP\u5C5E\u6027\uFF0C\u8FD9\u4F7F\u5F97\u5176\u53EF\u4EE5\u4F7F\u7528\u66F4\u591A\u7684\u9B54\u6CD5\u3002\u4F46\u662F\u540C\u6837\u7684\uFF0C\u654C\u4EBA\u65E0\u6CD5\u901A\u8FC7MP\u6062\u590DHP\u3002</p><p>\u4E00\u822C\u6765\u8BF4\uFF0C\u654C\u4EBA\u62E5\u6709\u56FA\u5B9A\u7684\u653B\u51FB\u6A21\u5F0F\u6216\u8005\u8BF4\u7279\u5B9A\u7684\u9B54\u6CD5\uFF0C\u8FD9\u4E9B\u9B54\u6CD5\u4E0D\u5FC5\u8981\u62D8\u675F\u5728\u9B54\u6CD5\u5C11\u5973\u7684\u9B54\u529B\u6846\u67B6\u4E0B\u3002\u6A21\u7EC4\u4F5C\u8005\u53EF\u4EE5\u8BBE\u5B9A\u4E00\u4E2A\u9B54\u6CD5\u6709\u5DE8\u5927\u7684\u653B\u51FB\u8303\u56F4\u6216\u662F\u4F24\u5BB3\uFF0C\u53EF\u80FD\u8FD9\u610F\u5473\u7740\u4F5C\u8005\u5E76\u4E0D\u5E0C\u671B\u73A9\u5BB6\u51FB\u8D25\u4ED6\u3002</p><p>\u6A21\u7EC4\u4F5C\u8005\u53EF\u4EE5\u901A\u8FC7\u6295\u63B7\u9B54\u6CD5\u5C11\u5973\u7684\u5C5E\u6027\u503C\u8FDB\u884C\u52A0\u5DE5\u83B7\u5F97\u654C\u4EBA\u7684\u5C5E\u6027\u503C\uFF0C\u5F53\u73A9\u5BB6\u4E2D\u6709\u4EBA\u53D8\u6210\u4E86\u534A\u9B54\u5973\u6216\u662F\u9B54\u5973\uFF0C\u4E5F\u53EF\u4EE5\u91C7\u53D6\u4E0B\u9762\u7684\u65B9\u5F0F\u4E34\u65F6\u8BBE\u7F6E\u4E00\u4E2A\u654C\u4EBA\u3002</p><p>\u4E00\u822C\u800C\u8A00\uFF0C\u4E0D\u5177\u6709\u610F\u8BC6\u7684\u654C\u4EBA\u5177\u6709\u5F3A\u70C8\u7684\u653B\u51FB\u6B32\u671B\uFF0C\u4ECE\u800C\u4E0D\u4F1A\u95EA\u907F\u9B54\u6CD5\u5C11\u5973\u7684\u653B\u51FB\u3002</p><h3 id="\u5D29\u574F\u5316\u8EAB" tabindex="-1"><a class="header-anchor" href="#\u5D29\u574F\u5316\u8EAB" aria-hidden="true">#</a> \u5D29\u574F\u5316\u8EAB</h3><p>\u5F53\u9B54\u6CD5\u5C11\u5973\u9677\u5165\u5D29\u574F\u65F6\uFF0C\u7075\u9B42\u5B9D\u77F3\u4E2D\u7684\u6C61\u79FD\u4F1A\u4ECE\u7075\u9B42\u5B9D\u77F3\u4E2D\u72EC\u7ACB\u51FA\uFF0C\u5F62\u6210\u4E0D\u5B8C\u5168\u7684\u60B2\u53F9\u4E4B\u79CD\u3002\u4E0E\u6B64\u540C\u65F6\uFF0C\u4E5F\u4F1A\u4EA7\u751F\u5D29\u574F\u5316\u8EAB\uFF0C\u8FD9\u662F\u4E00\u79CD\u4E0D\u5B8C\u5168\u7684\u9B54\u5973\uFF0C\u548C\u9B54\u5973\u4E00\u6837\u53EF\u4EE5\u5236\u4F5C\u7ED3\u754C\uFF0C\u4F46\u662F\u65E0\u6CD5\u50CF\u9B54\u5973\u4E00\u6837\u751F\u4EA7\u4F7F\u9B54\u3002</p><p>\u5728\u5D29\u574F\u5316\u8EAB\u7684\u72B6\u6001\u4E0B\uFF0C\u9B54\u529B\u7684\u9650\u5236\u4F1A\u88AB\u89E3\u9664\uFF0C\u8FD9\u4F7F\u5F97\u534A\u9B54\u5973\u7684\u5404\u9879\u5C5E\u6027\u90FD\u8981\u6BD4\u9B54\u6CD5\u5C11\u5973\u65F6\u66F4\u9AD8</p><p>\u5D29\u574F\u5316\u8EAB\u4EE3\u8868\u7740\u9B54\u6CD5\u5C11\u5973\u7684\u6F5C\u610F\u8BC6\uFF0C\u800C\u5D29\u574F\u7ED3\u754C\u7C7B\u4F3C\u4E8E\u9B54\u6CD5\u5C11\u5973\u7684\u68A6\u5883\u3002</p><p>\u5D29\u574F\u5316\u8EAB\u7684\u53C2\u8003\u5C5E\u6027\u503C\uFF1D\u9B54\u6CD5\u5C11\u5973\u7684\u5C5E\u6027\u503C\uFF0B\u81EA\u7531\u5206\u914D\u7684100\u70B9\u5C5E\u6027</p><p>\u5D29\u574F\u5316\u8EAB\u53EF\u80FD\u62E5\u6709\u9B54\u6CD5\u5C11\u5973\u7C7B\u4F3C\u7684\u5916\u8868\uFF0C\u4E5F\u53EF\u4EE5\u50CF\u9B54\u5973\u4E00\u6837\u5177\u6709\u5DE8\u5927\u7684\u4F53\u578B</p><p>\u5D29\u574F\u5316\u8EAB\u7684HP\u53D6\u6B63\u5E38\u503C\u76842\u500D\u4EE5\u4E0A</p><p>\u5D29\u574F\u5316\u8EAB\u7684\u72C2\u5316\u72B6\u6001\u76F8\u6BD4\u4E8E\u666E\u901A\u60C5\u51B5\u4E0B\u53EF\u80FD\u9020\u6210\u66F4\u9AD8\u7684\u4F24\u5BB3\u6216\u662F\u62E5\u6709\u66F4\u9AD8\u7684\u5C5E\u6027\uFF0C\u6A21\u7EC4\u4F5C\u8005\u548C\u4E3B\u6301\u4EBA\u53EF\u4EE5\u6309\u7167\u81EA\u5DF1\u7684\u4E60\u60EF\u81EA\u7531\u53D1\u6325</p><h3 id="\u5FC3\u9B54" tabindex="-1"><a class="header-anchor" href="#\u5FC3\u9B54" aria-hidden="true">#</a> \u5FC3\u9B54</h3><p>\u9B54\u6CD5\u5C11\u5973\u5728\u5171\u9E23\u7684\u8BB0\u5FC6\u4E2D\u53EF\u80FD\u4F1A\u770B\u5230\u5FC3\u9B54\uFF0C\u5FC3\u9B54\u662F\u4E0E\u5D29\u574F\u76F8\u5173\u7684\u67D0\u79CD\u4E8B\u7269\u7684\u5316\u8EAB\uFF0C\u4E00\u822C\u4F1A\u51FA\u73B0\u5728\u4E0E\u5D29\u574F\u76F8\u5173\u7684\u8BB0\u5FC6\u4E2D\u3002</p><p>\u5FC3\u9B54\u7684\u5C5E\u6027\u53EF\u4EE5\u53C2\u8003\u4F7F\u9B54\u76F8\u5BF9\u4E8E\u9B54\u5973\u7684\u80FD\u529B\u5173\u7CFB\u3002</p><p>\u4E0E\u5FC3\u9B54\u7684\u6218\u6597\u4F1A\u5728\u56DE\u5FC6\u4E2D\u8FDB\u884C\uFF0C\u8FD9\u4E5F\u610F\u5473\u7740\u9B54\u6CD5\u5C11\u5973\u5728\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\u65E0\u6CD5\u4E92\u76F8\u534F\u4F5C\u3002</p><p>\u4E0E\u5FC3\u9B54\u6218\u6597\u65F6\uFF0C\u6BCF\u56DE\u5408\u90FD\u4F1A\u635F\u59311\u70B9HP\uFF0C\u4E14\u65E0\u6CD5\u4F7F\u7528MP\u6062\u590DHP\uFF0C\u5F53\u9B54\u6CD5\u5C11\u5973\u7684HP\u5F520\u65F6\uFF0C\u89C6\u4E3A\u4E0E\u5FC3\u9B54\u7684\u6218\u6597\u5931\u8D25\u3002\u5F53\u9B54\u6CD5\u5C11\u5973\u4ECE\u4E0E\u5FC3\u9B54\u7684\u6218\u6597\u4E2D\u79BB\u5F00\u65F6\uFF0C\u6062\u590D\u8FDB\u5165\u6218\u6597\u524D\u7684HP\u3002\u4F46\u662F\u4E0E\u5FC3\u9B54\u6218\u6597\u4E2D\u6D88\u8017\u7684MP\u548CSAN\u4E0D\u4F1A\u6062\u590D\u3002\u4E0E\u5FC3\u9B54\u6218\u6597\u6210\u529F\u65F6\uFF0C\u73A9\u5BB6\u53EF\u4EE5\u7EE7\u7EED\u63A2\u7D22\u8BB0\u5FC6\uFF0C\u4F46\u662F\u4E0E\u5FC3\u9B54\u6218\u6597\u5931\u8D25\u65F6\uFF0C\u73A9\u5BB6\u4F1A\u88AB\u5F3A\u5236\u5F39\u51FA\u56DE\u5FC6\uFF0C\u5E76\u4F1A\u635F\u59311D3/1D6\u7684SAN\u503C\u3002</p><h3 id="\u9B54\u5973" tabindex="-1"><a class="header-anchor" href="#\u9B54\u5973" aria-hidden="true">#</a> \u9B54\u5973</h3><p>\u9B54\u5973\u7684\u53C2\u8003\u5C5E\u6027\u503C\uFF1D\u9B54\u6CD5\u5C11\u5973\u7684\u5C5E\u6027\u503C\uFF0B\u81EA\u7531\u5206\u914D\u7684200\u70B9\u5C5E\u6027</p><p>\u9B54\u5973\u7684\u4F53\u578B\u4E00\u822C\u548C\u9B54\u6CD5\u5C11\u5973\u65F6\u76F8\u5DEE\u751A\u8FDC\uFF0C\u5927\u90E8\u5206\u90FD\u5341\u5206\u5DE8\u5927\uFF0C\u4F46\u662F\u4E5F\u6709\u4F53\u578B\u975E\u5E38\u5C0F\u7684\u53EF\u80FD\u6027</p><p>\u9B54\u5973\u7684HP\u53D6\u6B63\u5E38\u503C\u76842\u500D\u4EE5\u4E0A</p><h3 id="\u4F7F\u9B54" tabindex="-1"><a class="header-anchor" href="#\u4F7F\u9B54" aria-hidden="true">#</a> \u4F7F\u9B54</h3><p>\u4F7F\u9B54\u7684\u53C2\u8003\u5C5E\u6027\u503C\uFF1D\u9B54\u6CD5\u5C11\u5973\u7684\u5C5E\u6027\u503C\uFF0B\u81EA\u7531\u5206\u914D\u768450\u70B9\u5C5E\u6027</p><p>\u4F7F\u9B54\u7684HP\u53D6\u6B63\u5E38\u503C\u5DE6\u53F3\u5373\u53EF\uFF0C\u82E5\u4F7F\u9B54\u6570\u91CF\u5F88\u5927\u53EF\u4EE5\u53D6\u6B63\u5E38\u7684\u534A\u503C\uFF0C\u4F46\u5927\u91CF\u7684\u4F7F\u9B54\u4F1A\u5BF9\u4E3B\u6301\u4EBA\u7684\u64CD\u4F5C\u9020\u6210\u8D1F\u62C5</p>',51),i=[d];function n(t,c){return e(),h("div",null,i)}var l=a(r,[["render",n],["__file","enemy.html.vue"]]);export{l as default};
